INSERT INTO `informasi` (`id`, `judul`, `keterangan`, `gambar`) VALUES
(28, 'Perbedaan Pohon Dengan Biomassa Besar & Kecil', '<h3><strong>Biomassa Besar</strong></h3>\r\n<ul>\r\n<li><strong>Jumlah Material Organik</strong>: Pohon dengan biomassa besar memiliki jumlah material organik yang lebih banyak, termasuk batang, cabang, daun, dan akar.</li>\r\n<li><strong>Ukuran Pohon</strong>: Umumnya, pohon dengan biomassa besar adalah pohon yang berukuran besar dengan batang tebal dan luas kanopi yang lebat.</li>\r\n<li><strong>Penyimpanan Karbon</strong>: Pohon dengan biomassa besar dapat menyimpan lebih banyak karbon karena memiliki lebih banyak material organik untuk mengikat karbon dari atmosfer.</li>\r\n<li><strong>Contoh</strong>: Pohon seperti ek (oak), baobab, dan redwood adalah contoh pohon dengan biomassa besar.</li>\r\n</ul>\r\n<h3><strong>Biomassa Kecil</strong></h3>\r\n<ul>\r\n<li><strong>Jumlah Material Organik</strong>: Pohon dengan biomassa kecil memiliki jumlah material organik yang lebih sedikit.</li>\r\n<li><strong>Ukuran Pohon</strong>: Umumnya, pohon dengan biomassa kecil adalah pohon yang berukuran kecil atau sedang, dengan batang yang lebih tipis dan kanopi yang lebih sedikit.</li>\r\n<li><strong>Penyimpanan Karbon</strong>: Pohon dengan biomassa kecil menyimpan karbon dalam jumlah yang lebih sedikit karena keterbatasan jumlah material organik yang dimiliki.</li>\r\n<li><strong>Contoh</strong>: Pohon seperti akasia, kaktus, atau pohon kecil lainnya biasanya memiliki biomassa yang lebih kecil.</li>\r\n</ul>\r\n<p>Secara umum, pohon dengan biomassa besar lebih efektif dalam menyerap dan menyimpan CO2 dalam jangka panjang dibandingkan dengan pohon yang memiliki biomassa kecil.</p>', 'informasi1724049417.jpg'),
(29, 'Ciri-Ciri Pohon Yang Menyerap C02 Dengan Efektif', '<p>Pohon yang menyerap CO2 dengan efektif biasanya memiliki beberapa ciri berikut:</p>\r\n<ol>\r\n<li>\r\n<p><strong>Laju Pertumbuhan Cepat</strong>: Pohon yang tumbuh cepat akan menyerap lebih banyak CO2 karena mereka menghasilkan lebih banyak biomassa dalam waktu singkat.</p>\r\n</li>\r\n<li>\r\n<p><strong>Biomassa Besar</strong>: Pohon dengan biomassa besar menyimpan lebih banyak karbon di dalam batang, daun, dan akarnya. Pohon besar dengan kayu yang padat biasanya lebih efektif dalam menyimpan karbon dalam jangka waktu yang lama.</p>\r\n</li>\r\n<li>\r\n<p><strong>Daun Lebat dan Luas</strong>: Pohon dengan daun yang banyak dan lebar memiliki area yang lebih besar untuk fotosintesis, yang berarti mereka dapat menyerap lebih banyak CO2 dari atmosfer.</p>\r\n</li>\r\n<li>\r\n<p><strong>Kayu Padat</strong>: Pohon yang memiliki kayu padat cenderung menyimpan karbon dalam jumlah yang lebih besar karena kayu padat mengandung lebih banyak massa per unit volume.</p>\r\n</li>\r\n<li>\r\n<p><strong>Umur Panjang</strong>: Pohon yang berumur panjang akan menyerap dan menyimpan CO2 dalam jangka waktu yang lama, sehingga lebih efektif dalam menyimpan karbon dibandingkan dengan pohon yang berumur pendek.</p>\r\n</li>\r\n<li>\r\n<p><strong>Jenis Daun</strong>: Pohon berdaun lebar (broadleaf) umumnya lebih efektif dalam fotosintesis dibandingkan pohon berdaun jarum (conifer), meskipun beberapa jenis pohon berdaun jarum juga dapat sangat efektif dalam menyerap CO2.</p>\r\n</li>\r\n</ol>\r\n<p>Contoh pohon yang memiliki ciri-ciri ini adalah ek (oak), mahoni, dan pinus.</p>', 'informasi1724049575.jpg');

